const { owner, name } = $('Load Repo Info').first().json;

const issueAnalysisOutputSchema = {
	title: "issue_analysis_output",
	description: "Analysis result of GitHub issues and latest release",
	type: "object",
	properties: {
		translationLanguageCode: {
			type: "string",
			description: "Language code"
		},
		latestRelease: {
			type: "object",
			properties: {
				details: {
					type: "array",
					items: {
						type: "object",
						properties: {
							category: {
								type: "string",
								description: "Category for the descriptions (e.g., 'breaking change', 'internal change')"
							},
							descriptions: {
								type: "array",
								items: { type: "string" },
								maxItems: 3,
								description: "Compressed release descriptions (maximum 3 items)"
							}
					},
					required: ["category", "descriptions"]
				},
				maxItems: 3,
				description: "Array of release details (maximum 3 items)"
				}
			},
			required: ["details"]
		},
		issues: {
			type: "array",
			items: {
				type: "object",
				properties: {
					issueTitle: { type: "string", description: "Issue title" },
					issueURL: {
						type: "string",
						pattern: `^https://github.com/${owner}/${name}/issues/\\d+$`,
						description: "Issue URL"
					},
					issueDescriptions: { 
						type: "array", 
						items: { type: "string" },
						description: "A 1-2 line concise summary of the original issue description capturing the core problem or feature request (NOT a verbatim copy)"
					},
					issueSuitability: {
						type: "object",
						properties: {
							level: {
								type: "string",
								enum: ["high", "medium", "low"],
								description: "Contribution opportunity level"
							},
							reasons: {
								type: "array",
								items: { type: "string" },
								description: "Reasons for the level"
							}
						},
						required: ["level", "reasons"]
					}
				},
				required: ["issueTitle", "issueURL", "issueDescription", "issueSuitability"]
			},
			description: "Top 3-5 suitable issues"
		}
	},
	required: ["translationLanguageCode", "latestRelease", "issues"]
};
return issueAnalysisOutputSchema

const TechnicalDifficultySchema = {
	type: "object",
	title: "technical_difficulty_assessment",
	description: "Schema for evaluating the technical complexity and difficulty level of resolving a GitHub issue",
	properties: {
		level: {
			type: "string",
			enum: ["High", "Medium", "Low"],
			description: "High | Medium | Low"
		},
		reasons: {
			type: "array",
			items: { type: "string" },
			description: "Reasons for the difficulty level"
		}
	},
	required: ["level", "reasons"]
};

const IssueResponseSchema = {
	type: "object",
	title: "deepwiki_issue_analysis_response",
	description: "Structured response schema containing comprehensive analysis of GitHub issues including root cause, resolution approach, difficulty assessment, and explanatory content",
	properties: {
		translationLanguageCode: {
			type: "string",
			description: "Language code"
		},
		deepwikiLink: {
			type: "string",
			pattern: "^https://deepwiki\\.com/search/.*",
			errorMessage: "Must start with https://deepwiki.com/search/"
		},
		rootCauses: {
			type: "array",
		items: { type: "string" },
			description: "Root cause of the issue"
		},
		resolutionApproach: {
			type: "array",
			items: { type: "string" },
			description: "List of resolution approaches"
		},
		technicalDifficulty: {
			...TechnicalDifficultySchema,
			description: "Technical difficulty assessment"
		},
		summary: {
			type: "string",
			description: "One-sentence contribution summary"
		},
		keyword: {
			type: "array",
			items: { type: "string" },
			description: "1-5 relevant keywords"
		},
		analogies: {
			type: "array",
			items: { type: "string" },
			description: "Simple analogy for issue and resolution"
		}
	},
	required: ["translationLanguageCode", "deepwikiLink", "rootCause", "resolutionApproach", "technicalDifficulty", "summary", "keyword", "analogy"]
};
return IssueResponseSchema;

return {
		title: "newsletter_title_output",
		description: "Generated newsletter title based on GitHub issue",
		type: "object",
		properties: {
				translationLanguageCode: {
						type: "string",
						description: "Language code"
				},
				title: {
						type: "string",
						description: "Catchy newsletter title with emoji, translated to target language"
				}
		},
		required: ["translationLanguageCode", "title"]
};
